#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <unistd.h>
#include <fcntl.h>

int main()
{  
    int fd;
    int i = 1048576;
    char path[] = "./helloworld.txt";
    char text[] = "";
    int ret;
    fd = open(path, O_CREAT | O_WRONLY, 0664);
    if (fd < 0)
    {
        printf("open w failed");
    }
    while (i > 0) {
        ret = write( fd, &text, strlen(text));
        if (ret < 0)
            break;
        i--;
    }
    close( fd );
    if (i != 0)
        printf("Write error!\n");
}
